<?phpecho '<h3>User administration</h3>';$_SESSION['csrfToken'] = $token = base64_encode( openssl_random_pseudo_bytes(32));if ( !$user->isRegional() ) {    $sql = "SELECT *, user.id AS userid, centre.name AS centre_name FROM user INNER JOIN centre ON user.centre_id = centre.id WHERE privilege_id='98'";        $search = DB::query( $sql );    if( $search->getRows() ) {        echo '<form action="process.php" method="post">';        write_user_table( $search, 'Select users to verify as local administrators' );        echo "<input type=\"hidden\" name=\"page\" value=\"useradm\">";        echo "<input type=\"hidden\" name=\"admin\" value=\"admin\">";        echo "<input type=\"hidden\" name=\"csrfToken\" value=\"{$token}\"/>";        echo "<div class=\"form-actions\">            <button type=\"submit\" class=\"btn btn-primary\">Submit</button>            </div>";        echo '</form>';    }}$sql = "SELECT *, user.id AS userid, centre.name AS centre_name FROM user INNER JOIN centre ON user.centre_id = centre.id WHERE privilege_id='99'";$pA = array();if ( $user->isRegional() ) {	$sql .= " AND centre.id = ?";	$pA = array( 's', $user->getCentre() );}$search = DB::query( $sql, $pA );if( !$search->getRows() ) echo "<h4>No users awaiting data entry validation</h4>";else {	echo '<form action="process.php" method="post">';	write_user_table( $search, 'Select users to verify for data entry access' );	echo "<input type=\"hidden\" name=\"page\" value=\"useradm\">";    echo "<input type=\"hidden\" name=\"csrfToken\" value=\"{$token}\"/>";	echo "<div class=\"form-actions\">		<button type=\"submit\" class=\"btn btn-primary\">Submit</button>		</div>";	echo '</form>';}?>